import React, { useState } from 'react';
import { FaAsterisk } from 'react-icons/fa';
import '../CSS/contact.css';

const ContactUs = () => {
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    email: '',
    mobno: '',
    message: '',
  });

  const handleInputChange = (event) => {
    setFormData((prevData) => ({
      ...prevData,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const { firstname, lastname, email, mobno, message } = formData;

    const formUrl =
      'https://docs.google.com/forms/d/e/1FAIpQLSeZIi1b1WUh1AESMMZyZRvtyn4TGiQFo1GYIhXFJTI2cV8bUA/viewform?usp=sf_link'; // Replace with your Google Form URL

    const queryParams = new URLSearchParams();
    queryParams.append('entry.1457482359', firstname); // Replace entry.1234567890 with the corresponding field ID for 'name' in your Google Form
    queryParams.append('entry.2078296727', lastname);
    queryParams.append('entry.2013843401', email); // Replace entry.0987654321 with the corresponding field ID for 'email' in your Google Form
    queryParams.append('entry.461866479', mobno);
    queryParams.append('entry.1603081233', message); // Replace entry.2468135790 with the corresponding field ID for 'message' in your Google Form

    const formSubmissionUrl = `${formUrl}?${queryParams.toString()}`;

    window.location.href = formSubmissionUrl;
  };

  return (
    <div>
      <h1 className="contact-heading">Contact Form</h1>
      <p className = "par">Please fill out the form to reach out to us.</p>
      <div className="contact-form">
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="firstname">
              First Name <FaAsterisk className="fa-icons" /> :
            </label>
            <input
              type="text"
              id="firstname"
              name="firstname"
              value={formData.firstname}
              onChange={handleInputChange}
              required
            />
          </div>

          <div>
            <label htmlFor="lastname">
              Last Name <FaAsterisk className="fa-icons" /> :
            </label>
            <input
              type="text"
              id="lastname"
              name="lastname"
              value={formData.lastname}
              onChange={handleInputChange}
              required
            />
          </div>

          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
            />
          </div>

          <div>
            <label htmlFor="mobno">
              Mobile Number <FaAsterisk className="fa-icons" /> :
            </label>
            <input
              type="tel"
              id="mobno"
              name="mobno"
              value={formData.mobno}
              onChange={handleInputChange}
              required
            />
          </div>

          <div>
            <label htmlFor="message">
              Message <FaAsterisk className="fa-icons" /> :
            </label>
            <textarea
              id="message"
              name="message"
              value={formData.message}
              onChange={handleInputChange}
              required
            ></textarea>
          </div>
          <button className = "contact-btn" type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default ContactUs;
