import React, { useEffect, useState } from 'react';
import homeImg from "../Assets/Image1.jpg";
import weddingImg from "../Assets/Image3.jpg";
import bollywoodNightImg from "../Assets/Image2.jpg";
import clubsBarsImg from "../Assets/Image4.jpg";
import "../CSS/homeComponent.css";
import { getEvents } from 'react-google-calendar-api';

const HomeComponent = () => {
    const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchEvents = async () => {
      const result = await getEvents({
        calendarId: 'YOUR_CALENDAR_ID',
        timeMin: new Date().toISOString(),
        maxResults: 5, // Adjust the number of upcoming events to display
        orderBy: 'startTime',
        singleEvents: true,
      });

      setEvents(result);
    };

    fetchEvents();
  }, []);

  return (
    <>
    <div className="homeimg">
      <div className="left-column">
        <img src = {homeImg} alt = "Home"/>
      </div>
      <div className="right-column">
        <div className="content">
          <h2>Adaaah</h2>
          <p>Bollywood DJ | Entertainment | Design</p>
        </div>
      </div>
    </div>

    <div className="dj-services">
        <h2 className="heading">DJ Services</h2>
        <div className="column">
            <div className="circle">
                <img src = {weddingImg} alt="Wedding" />
            </div>
                <h3>Wedding</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </div>
        <div className="column">
            <div className="circle">
                <img src = {bollywoodNightImg} alt="Bollywood Night" />
            </div>
                <h3>Bollywood Night</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </div>
        <div className="column">
            <div className="circle">
                <img src = {clubsBarsImg} alt="Clubs and Bars" />
            </div>
                <h3>Clubs and Bars</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </div>
        </div>

        <div className="upcoming-events">
        <h2>Upcoming Events</h2>
        <ul>
            {events.map((event) => (
            <li key={event.id}>
                <h3>{event.summary}</h3>
                <p>{event.start.dateTime}</p>
            </li>
            ))}
        </ul>
        </div>
    </>
  );
};


export default HomeComponent;
