import React, { useState, useEffect } from 'react';
import { FaAsterisk } from 'react-icons/fa';
import '../CSS/contact.css';
import {FaArrowDown} from "react-icons/fa"

const ContactUs = () => {
  const [currentDay, setCurrentDay] = useState('');
  const [showAllDays, setShowAllDays] = useState(false);
  const hoursSchedule = [
    { day: 'Monday', hours: '9 AM – 5 PM' },
    { day: 'Tuesday', hours: '9 AM – 5 PM' },
    { day: 'Wednesday', hours: '9 AM – 5 PM' },
    { day: 'Thursday', hours: '9 AM – 5 PM' },
    { day: 'Friday', hours: '9 AM – 5 PM' },
    { day: 'Saturday', hours: 'Closed' },
    { day: 'Sunday', hours: 'Closed' }
  ];

  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    email: '',
    mobno: '',
    message: '',
    formId: 'form2',
  });

  useEffect(() => {
    const today = new Date().toLocaleString('en-us', { weekday: 'long' });
    setCurrentDay(today);
  }, []);

  const handleToggleDays = () => {
    setShowAllDays((prevShowAllDays) => !prevShowAllDays);
  };

  const resetForm = () => {
    setFormData({
      firstname: '',
      lastname: '',
      email: '',
      mobno: '',
      message: '',
      formId: 'form2',
    });
  };

  const handleInputChange = (event) => {
    setFormData((prevData) => ({
      ...prevData,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const { firstname, lastname, email, mobno, message } = formData;

    const postData = {
      firstname,
      lastname,
      email,
      mobno,
      message,
      formId: 'form2',
    };

    fetch('http://localhost:3001/write-to-spreadsheet', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(postData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        console.log('Data sent successfully!');
        // You can add any success message or redirection logic here if needed.
        resetForm();
      })
      .catch((error) => {
        console.error('Error sending data:', error);
        // You can handle errors and display an error message if needed.
      });
  };

  return (
    <div>
      <h1 className="contact-heading">Contact Form</h1>
      <p className = "par">Please fill out the form to reach out to us.</p>
      <div className="contact-form">
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="firstname">
              First Name <FaAsterisk className="fa-icons" /> :
            </label>
            <input
              type="text"
              id="firstname"
              name="firstname"
              value={formData.firstname}
              onChange={handleInputChange}
              required
            />
          </div>

          <div>
            <label htmlFor="lastname">
              Last Name <FaAsterisk className="fa-icons" /> :
            </label>
            <input
              type="text"
              id="lastname"
              name="lastname"
              value={formData.lastname}
              onChange={handleInputChange}
              required
            />
          </div>

          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
            />
          </div>

          <div>
            <label htmlFor="mobno">
              Mobile Number <FaAsterisk className="fa-icons" /> :
            </label>
            <input
              type="tel"
              id="mobno"
              name="mobno"
              value={formData.mobno}
              onChange={handleInputChange}
              required
            />
          </div>

          <div>
            <label htmlFor="message">
              Message <FaAsterisk className="fa-icons" /> :
            </label>
            <textarea
              id="message"
              name="message"
              value={formData.message}
              onChange={handleInputChange}
              required
            ></textarea>
          </div>
          <button className = "contact-btn" type="submit">Submit</button>
        </form>
      </div>

      <div className = "opening-hours">
        {showAllDays ? (
          hoursSchedule.map(({ day, hours }) => (
            <div key={day}>
              {day === currentDay ? `${day}: ${hours}` : `${day}: Closed`}
            </div>
          ))
        ) : (
          <div>
            {hoursSchedule.map(({ day, hours }) => (
              day === currentDay ? (
                <div key={day}>
                  {day}: {hours}
                </div>
              ) : null
            ))}
          </div>
          )}
        <button onClick={handleToggleDays}>
          {showAllDays ? 'Hide Timings' : 'Show All Timings'}<FaArrowDown/>
        </button>
      </div>

    </div>
  );
};

export default ContactUs;
