import React, {useState} from 'react';
import "../CSS/loginPage.css";
import Logo from "../Assets/Images/Image5.jpg";
import GoogleLoginButton from './googleLoginButton';
import { FaAsterisk } from 'react-icons/fa';
import {Link} from "react-router-dom";

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleGoogleSuccess = (response) => {
    // Handle the successful Google Sign-In here (e.g., send the token to the server)
    console.log('Google Sign-In successful:', response);
  };

  const handleGoogleFailure = (error) => {
    // Handle the failed Google Sign-In here
    console.error('Google Sign-In failed:', error);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    // Handle the login logic here (e.g., send login credentials to the server)
    console.log('Email:', email);
    console.log('Password:', password);
  };
  return (
    <div className="login-page">
      <div className="left-container">
        <div className="google-signin">
          <GoogleLoginButton onSuccess={handleGoogleSuccess} onFailure={handleGoogleFailure} />
        </div>
        <h3 className = "or">OR</h3>
        <div className = "login-container">
          <form className = "login-form" onSubmit={handleLogin}>
            <h2>Login</h2>
            <div>
              <label htmlFor="email">Email: <FaAsterisk className="fa-icons" /></label>
              <input
                type="email"
                id="email"
                value = {email}
                name="email"
                onChange={(e) => setEmail(e.target.value)} required
              />
            </div>

          <div>
            <label htmlFor="Password">Password: <FaAsterisk className="fa-icons" /></label>
            <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)} required
              />
          </div>
          <Link className = "forgot-password" to="/forget-password">Forgot Password?</Link>
          <div className = "submit-container">
            <button className = "login-btn" type="submit">Submit</button>
          </div>

          <h1>Don't have an account?</h1>
          <button className = "sign-up" type="submit">Sign-up</button>

        </form>
        </div>
      </div>
      <div className="right-container">
        <div className="logo-img">
          <img src={Logo} alt="Logo" />
        </div>
      </div>
    </div>

  )
}

export default LoginPage;
